const Trigger = styled.button`
  display: flex;
  padding: 0.25rem 0.625rem 0rem 0rem;
  align-items: center;
  justify-content: space-between;
  gap: 0.6875rem;
  width: 100%;
  color: var(--ui-elements-dark, #11181c);
  font-family: "Mona Sans";
  font-size: 0.875rem;
  font-style: normal;
  font-weight: 600;
  line-height: 142%; /* 1.2425rem */
  background: none;
  border: none;

  & > div {
    display: flex;
    align-items: center;
    gap: 0.6875rem;
    justify-content: flex-start;

    & > svg {
      color: #979fa4;
    }
  }

  & > svg {
    transition: transform 0.2s ease-in-out;
    transform: rotate(180deg);
  }

  &.open {
    & > svg {
      transform: rotate(-0deg);
    }
  }
`;

const collapse = styled.keyframes`
  from {
    height: 100%;
  }
  to {
    height: 0;
  }
`;

const expand = styled.keyframes`
  from {
    height: 0;
  }
  to {
    height: 100%;
  }
`;

const Content = styled.div`
  transition: height 250ms, transform 250ms ease;
  transform-origin: top;
  overflow: hidden;

  &.closed {
    /* animation: ${collapse} 250ms ease; */
    transform: scaleY(0);
    height: 0;
  }

  &.open {
    /* animation: ${expand} 250ms ease; */
    transform: scaleY(1);
    height: 100%;
  }
`;

const Sub = styled.div`
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  justify-content: flex-start;
  gap: 0.62rem;
  padding-left: 1.875rem;
  padding-top: 0.81rem;

  & > div {
    width: 100%;
  }
`;

